from trex_stl_lib.api import *

class STLS1(object):

    def create_stream (self):
        input_binary = ""
        binary = binascii.unhexlify(input_binary)
        return STLStream( 
            packet = STLPktBuilder(pkt = Ether(binary, dst='01:80:c2:00:00:14')),
            mode = STLTXCont())

    def get_streams (self, direction = 0, **kwargs):
        # create 1 stream 
        return [ self.create_stream() ]


# dynamic load - used for trex console or simulator
def register():
    return STLS1()



